import React, {useState} from 'react';
import {Router, Link, navigate} from '@reach/router';
import axios from 'axios';
import moment from 'moment';




const NewProject = props => {

const [project, setProject] = useState("");
const [date, setDate] = useState("");
const [errors, setErrors] = useState({})


const addProjects = e => {
    e.preventDefault();
    const projects = {project, date}
    axios.post("http://localhost:8000/api/projects/new", projects)
        .then(res => {
            if(res.data.errors) {
                setErrors(res.data.errors);

            }else{
                navigate("/projects")
            }
        })
        .catch(err => console.log(err) );

}
    return (
        <form onSubmit={ addProjects }>
            <h1 style ={{fontSize: "40px", marginLeft: "200px"}}>Plan a new project</h1>
            <Link style ={{float: "right"}}to ="/"> Back to Dashboard</Link>
            <div className="field">
                <div className="control">
                    <input  style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="text" placeholder="project" onChange = {e => setProject(e.target.value)} value ={project} />
                   { 
                       errors.project ?
                       <p>{errors.project.message}</p> :
                       ""
                   }
                </div>
            </div>   
            <div className="field">
                <div className="control">
                <input style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="date" onChange = {e => setDate(e.target.value)} value={moment(date).format("YYYY-MM")} />
                { 
                       errors.date ?
                       <p>{errors.project.date}</p> :
                       ""
                   }
              </div>
          </div>  
            <button  style ={{marginLeft: "150px"}} className="button is-primary is outlined" type="submit"> <Link to ="/">Plan a project </Link> </button>
        </form>
    )
}


 export default NewProject;
