import React, {useState, useEffect} from 'react';
import {Router, Link} from '@reach/router';
import axios from 'axios';
import moment from 'moment';


const Status = props => {
    const [projects, setProjects] = useState([]);

    const displayProjects = () => {
        axios.get("http://localhost:8000/api/projects")
            .then(res => setProjects(res.data))
            .catch(err => console.log(err))
    }

    const[isStart, setIsStart] = useState("false");

    const handleClick = () => {
        setIsStart
    }

    useEffect( () => {
        displayProjects();

    }, []);
    return (
    <div className="Wrapper">
    <div className="columns is-gapless">
    <div className = "column"> 
        <article className="message is-success">
        <div className="message-header">
            <p>Backlog</p>
        </div>
        {projects.map(pro => 
        <div className="message-body"  key={pro._id}>
           <h5>{pro.project}</h5>
           <p>Due: {moment(pro.date).format('MM-DD-YYYY')}</p> 
            <button className="button is-primary is outlined" onClick={handleClick}> Start Project > </button>
        </div> 
        )}
        </article>
    </div>
    <div className = "column">
            <article className="message is-success ">
        <div className="message-header">
            <p>In Progress</p>
        </div>
        <div className="message-body">
        <h1></h1>
        </div>
        </article>
    </div>
    <div className = "column">
            <article className="message is-success">
        <div className="message-header">
            <p>Completed</p>
        </div>
        <div className="message-body">
        <h1></h1>
        </div>
        </article>
    </div>
    </div>
    <button className="button is-primary"> <Link to ="/projects/new"> Add New Projects</Link></button>
    </div>
    )
}

export default Status;
