import React, {useState} from 'react';
import {Router, Link} from '@reach/router';
import axios from 'axios';



const NewProject = props => {

const [project, setProject] = useState("");
const [date, setDate] = useState("");

const addProjects = e => {
    e.preventDefault();
    const projects = {project, date}
    axios.post("http://localhost:8000/api/projects", projects)
        .then(res => console.log(res))
        .catch(err, console.log(err))


}
    return (
        <form onSubmit={ addProjects }>
            <h1 style ={{fontSize: "40px", marginLeft: "200px"}}>Plan a new project</h1>
            <div className="field">
                <div className="control">
                    <input style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="text" placeholder="project" onChange = {e => setProject(e.target.value)} />
                </div>
            </div>   
            <div className="field">
                <div className="control">
                <input style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="date" onChange = {e => setDate(e.target.value)} />
              </div>
          </div>   
            <button style ={{width: "60%", marginLeft: "150px"}} className="button is-primary is outlined" type="submit">Plan a project</button>
        </form>
    )
}


 export default NewProject;
