import React, {useState} from 'react';
import {Router, Link} from '@reach/router';
import axios from 'axios';




const NewProject = props => {

const [project, setProject] = useState("");
const [date, setDate] = useState("");

const handleChange = e =>{
    setProject({
      ...project
    })

  }

const addProjects = e => {
    e.preventDefault();
    const projects = {project, date}
    axios.post("http://localhost:8000/api/projects/new", projects)
        .then(res => console.log(res))
        .catch(err => console.log(err) );

}
    return (
        <form onSubmit={ addProjects }>
            <h1 style ={{fontSize: "40px", marginLeft: "200px"}}>Plan a new project</h1>
            <Link style ={{float: "right"}}to ="/"> Back to Dashboard</Link>
            <div className="field">
                <div className="control">
                    <input onChange = {handleChange} name="cProject" style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="text" placeholder="project" onChange = {e => setProject(e.target.value)} value ={project.c} />
                    {project.cProject.length < 2 ? <p>First name must be at least 2 letters</p> : "" }
                </div>
            </div>   
            <div className="field">
                <div className="control">
                <input style ={{width: "50%" , marginLeft: "200px"}} className="input is-primary" type="date" onChange = {e => setDate(e.target.value)} />
              </div>
          </div>  
            <button  style ={{width: "60%", marginLeft: "150px"}} className="button is-primary is outlined" type="submit"> <Link to ="/">Plan a project </Link> </button>
        </form>
    )
}


 export default NewProject;
